
public class math_query {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.nextUp(10.22));
		System.out.println(Math.ceil(10.35));
		System.out.println(Math.floor(10.99));
		System.out.println(Math.nextDown(10.22));
		System.out.println(Math.nextAfter(10.44, .01));
		System.out.println(Math.nextAfter(10.44, .001));
		System.out.println(Math.random());

	}

}
