
public class primitive_datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 9, b =2;
		float f;
		
		f = a/b;
		System.out.println("value of f" + f);
		f = f/2;
		System.out.println("value of f" + f);
		f = a + b / f;
		System.out.println("value of f" + f);
		
	}

}
